import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./CheckInPage.css";

const CheckInPage = () => {
  const [checkInStatus, setCheckInStatus] = useState("Not Checked In");
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();
  const [isAtWork, setIsAtWork] = useState(false);
  const [location, setLocation] = useState(null);
  const [workLocation, setWorkLocation] = useState(null);
  const [hasCheckedIn, setHasCheckedIn] = useState(false);

  useEffect(() => {
    // Optional: Fetch initial check-in status on page load
    const fetchCheckInStatus = async () => {
      // ... (add logic to fetch status from your backend)
    };
    // fetchCheckInStatus();
  }, []);

  

  const handleCheckIn = async () => {
    setIsLoading(true);

    try {
      const token = localStorage.getItem("token");
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };

      const response = await axios.post("/api/checkin", {}, config);
      setCheckInStatus("Checked In!");
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      if (error.response && error.response.status === 401) {
        // Handle unauthorized (e.g., redirect to login)
        navigate("/login");
      } else {
        setErrorMessage("Check-in failed. Please try again.");
      }
    }
  };

  return (
    <div className="checkin-page-container">
      {" "}
      {/* Add styling */}
      <h1>Check-In</h1>
      {isLoading && <p>Checking in...</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <p>{checkInStatus}</p>
      <button onClick={handleCheckIn} disabled={isLoading}>
        Check In
      </button>
    </div>
  );
};

export default CheckInPage;
